import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';
import { registerUser, loginUser, getVehicles, createBooking, getMyBookings, deleteBooking } from './apiService';
import AuthModal from './AuthModal';

// --- Reusable Components ---
function Toast({ message, type }) {
    return <div className={`toast ${type}`}>{message}</div>;
}

// NEW: A reusable confirmation modal component
function ConfirmationModal({ isOpen, onClose, onConfirm, title, message }) {
    if (!isOpen) return null;

    return (
        <div className="modal-backdrop">
            <div className="confirm-modal-content">
                <h3>{title}</h3>
                <p>{message}</p>
                <div className="confirm-modal-actions">
                    <button className="btn" onClick={onClose}>Cancel</button>
                    <button className="btn btn-danger" onClick={onConfirm}>Confirm</button>
                </div>
            </div>
        </div>
    );
}


// --- Custom Hooks ---
function useToast() {
    const [toast, setToast] = useState({ message: '', type: '', visible: false });
    const showToast = (message, type = 'success') => {
        setToast({ message, type, visible: true });
        setTimeout(() => setToast(t => ({ ...t, visible: false })), 3000);
    };
    return { toast, showToast };
}

// --- Main App Component ---
function App() {
    const [page, setPage] = useState('home');
    const [user, setUser] = useState(null);
    const [modalMode, setModalMode] = useState(null);
    const { toast, showToast } = useToast();

    useEffect(() => { /* ... Unchanged ... */ }, []);
    const handleAuthSubmit = async (userData) => { /* ... Unchanged ... */ };
    const handleLogout = () => { /* ... Unchanged ... */ };

    const renderPage = () => {
        switch (page) {
            case 'dashboard':
                return user ? <DashboardPage showToast={showToast} /> : <HomePage user={user} showToast={showToast} />;
            case 'stations':
                return <ChargingStationsPage />;
            case 'map':
                return <CampusMapPage />;
            case 'contact':
                return <ContactUsPage />;
            case 'home':
            default:
                return <HomePage user={user} showToast={showToast} />;
        }
    };

    return (
        <>
            {toast.visible && <Toast message={toast.message} type={toast.type} />}
            {modalMode && <AuthModal mode={modalMode} onSubmit={handleAuthSubmit} onClose={() => setModalMode(null)} showToast={showToast} />}
            <Header user={user} setPage={setPage} onLogin={() => setModalMode('login')} onRegister={() => setModalMode('register')} onLogout={handleLogout} />
            <main>
                {renderPage()}
            </main>
            <Footer setPage={setPage} />
        </>
    );
}

// --- Page & Layout Components ---

function Header({ user, setPage, onLogin, onRegister, onLogout }) { /* ... Unchanged ... */ }
function HomePage({ user, showToast }) { /* ... Unchanged ... */ }

// UPDATED: DashboardPage now uses the ConfirmationModal
function DashboardPage({ showToast }) {
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);

    // NEW: State for the confirmation modal
    const [isConfirmOpen, setConfirmOpen] = useState(false);
    const [bookingToDelete, setBookingToDelete] = useState(null);

    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const response = await getMyBookings();
                setBookings(response.data);
            } catch (error) {
                showToast('Could not load your bookings.', 'error');
            } finally {
                setLoading(false);
            }
        };
        fetchBookings();
    }, [showToast]);

    // NEW: Function to open the confirmation modal
    const handleCancelClick = (bookingId) => {
        setBookingToDelete(bookingId);
        setConfirmOpen(true);
    };

    // NEW: Function that runs when the user confirms the deletion
    const confirmDelete = async () => {
        try {
            await deleteBooking(bookingToDelete);
            setBookings(bookings.filter(b => b.id !== bookingToDelete));
            showToast('Booking cancelled successfully.', 'success');
        } catch (error) {
            const errorMessage = error.response?.data?.error || 'Failed to cancel booking.';
            showToast(errorMessage, 'error');
        } finally {
            setConfirmOpen(false);
            setBookingToDelete(null);
        }
    };

    return (
        <>
            <ConfirmationModal
                isOpen={isConfirmOpen}
                onClose={() => setConfirmOpen(false)}
                onConfirm={confirmDelete}
                title="Cancel Booking"
                message="Are you sure you want to permanently cancel this vehicle booking?"
            />
            <div className="page-container">
                <h2 className="page-header">My Bookings Dashboard</h2>
                {loading && <p>Loading your bookings...</p>}
                {!loading && bookings.length === 0 && (
                    <div className="card-message">You have not made any bookings yet.</div>
                )}
                <div className="booking-list">
                    {bookings.map(b => (
                        <div className="booking-card" key={b.id}>
                            <img src={b.vehicleImageUrl} alt={b.vehicleName} />
                            <div className="booking-info">
                                <h4>{b.vehicleName}</h4>
                                <p>Booked on: {new Date(b.bookingTime).toLocaleString()}</p>
                            </div>
                            <button className="btn-cancel" onClick={() => handleCancelClick(b.id)}>Cancel</button>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}

function ChargingStationsPage() { /* ... Unchanged ... */ }
function CampusMapPage() { /* ... Unchanged ... */ }
function ContactUsPage() { /* ... Unchanged ... */ }
function Footer({ setPage }) { /* ... Unchanged ... */ }


// --- Helper Functions to keep the main component cleaner ---
// All of these are now included in the blocks above for completeness
// ...

export default App;
