import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';
import { registerUser, loginUser, getVehicles, createBooking, getMyBookings, deleteBooking } from './apiService';
import AuthModal from './AuthModal';

// --- NEW: A reusable Toast component ---
function Toast({ message, type }) {
    return <div className={`toast ${type}`}>{message}</div>;
}

// --- NEW: A custom hook to manage the toast ---
function useToast() {
    const [toast, setToast] = useState({ message: '', type: '', visible: false });

    const showToast = (message, type = 'success') => {
        setToast({ message, type, visible: true });
        setTimeout(() => {
            setToast(t => ({ ...t, visible: false }));
        }, 3000); // Hide after 3 seconds
    };

    return { toast, showToast };
}

// --- Main App Component ---
function App() {
    const [page, setPage] = useState('home');
    const [user, setUser] = useState(null);
    const [modalMode, setModalMode] = useState(null);
    const { toast, showToast } = useToast(); // Using the new toast hook

    useEffect(() => { /* ... this useEffect is unchanged ... */ }, []);

    const handleAuthSubmit = async (userData) => {
        const apiCall = modalMode === 'register' ? registerUser : loginUser;
        const response = await apiCall(userData);
        const token = response.data.token;
        localStorage.setItem('authToken', token);
        const decodedUser = jwtDecode(token);
        setUser({ name: userData.name || decodedUser.sub.split('@')[0], email: decodedUser.sub });
        setModalMode(null);
        showToast(`Welcome, ${decodedUser.sub.split('@')[0]}!`, 'success');
    };

    const handleLogout = () => { /* ... this function is unchanged ... */ };

    const renderPage = () => {
        switch (page) {
            case 'dashboard':
                return user ? <DashboardPage showToast={showToast} /> : <HomePage user={user} showToast={showToast} />;
            case 'stations':
                return <ChargingStationsPage />;
            case 'map':
                return <CampusMapPage />;
            case 'contact':
                return <ContactUsPage />;
            case 'home':
            default:
                return <HomePage user={user} showToast={showToast} />;
        }
    };

    return (
        <>
            {toast.visible && <Toast message={toast.message} type={toast.type} />}
            {modalMode && <AuthModal mode={modalMode} onSubmit={handleAuthSubmit} onClose={() => setModalMode(null)} showToast={showToast} />}
            <Header user={user} setPage={setPage} onLogin={() => setModalMode('login')} onRegister={() => setModalMode('register')} onLogout={handleLogout} />
            <main>
                {renderPage()}
            </main>
            <Footer setPage={setPage} />
        </>
    );
}

// --- Page & Layout Components ---

function Header({ user, setPage, onLogin, onRegister, onLogout }) { /* ... This component is unchanged ... */ }

function HomePage({ user, showToast }) {
    const [vehicles, setVehicles] = useState([]);
    const [filter, setFilter] = useState('all');

    useEffect(() => { /* ... this useEffect is unchanged ... */ }, []);

    const handleBooking = async (vehicleId) => {
        try {
            await createBooking(vehicleId);
            showToast('Vehicle booked successfully! Check your dashboard.', 'success');
        } catch (error) {
            const errorMessage = error.response?.data?.error || 'An unexpected error occurred.';
            showToast(errorMessage, 'error'); // Use toast instead of alert
        }
    };

    const filteredVehicles = vehicles.filter(v => filter === 'all' || v.type === filter);

    return (
      <section className="hero">
        {/* ... The rest of the HomePage JSX is the same, just ensure handleBooking is correct ... */}
      </section>
    );
}

function DashboardPage({ showToast }) {
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => { /* ... this useEffect is unchanged ... */ }, []);

    const handleDeleteBooking = async (bookingId) => {
        if (window.confirm("Are you sure you want to cancel this booking?")) {
            try {
                await deleteBooking(bookingId);
                setBookings(bookings.filter(b => b.id !== bookingId));
                showToast('Booking cancelled successfully.', 'success');
            } catch (error) {
                const errorMessage = error.response?.data?.error || 'Failed to cancel booking.';
                showToast(errorMessage, 'error'); // Use toast instead of alert
            }
        }
    };

    return (
        <div className="page-container">
            <h2 className="page-header">My Bookings Dashboard</h2>
            {/* ... The rest of the DashboardPage JSX is the same, just ensure handleDeleteBooking is correct ... */}
        </div>
    );
}

// --- NEW, IMPROVED ChargingStationsPage ---
function ChargingStationsPage() {
    const [filter, setFilter] = useState('All');
    const stations = [
        { name: "Library Parking Garage", details: "4 Level 2 Chargers", status: "Available" },
        { name: "Student Union Center", details: "2 DC Fast Chargers", status: "In Use" },
        { name: "Science Building Lot D", details: "6 Level 2 Chargers", status: "Available" },
        { name: "Sports Complex Lot A", details: "8 Level 2 Chargers", status: "Maintenance" },
        { name: "North Campus Dorms", details: "4 Level 2 Chargers", status: "Available" },
    ];

    const filteredStations = stations.filter(s => filter === 'All' || s.status === filter);

    return (
        <div className="page-container">
            <h2 className="page-header">Campus Charging Stations</h2>
            <div className="filter-tabs">
                <button onClick={() => setFilter('All')} className={filter === 'All' ? 'active' : ''}>All</button>
                <button onClick={() => setFilter('Available')} className={filter === 'Available' ? 'active' : ''}>Available</button>
                <button onClick={() => setFilter('In Use')} className={filter === 'In Use' ? 'active' : ''}>In Use</button>
            </div>
            <div className="booking-list">
                {filteredStations.map(station => (
                    <div className="booking-card" key={station.name}>
                        <div className="booking-info">
                            <h4>{station.name}</h4>
                            <p>{station.details}</p>
                        </div>
                        <div className="status-tag">
                            <span className={`status-indicator status-${station.status.toLowerCase().replace(' ', '-')}`}></span>
                            {station.status}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}


function CampusMapPage() { /* ... This component is unchanged ... */ }
function ContactUsPage() { /* ... This component is unchanged ... */ }
function Footer({ setPage }) { /* ... This component is unchanged ... */ }

export default App;
