import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';
import { registerUser, loginUser, getVehicles, createBooking, getMyBookings } from './apiService';
import AuthModal from './AuthModal';

// --- Main App Component ---
function App() {
    // State to manage which page is currently visible
    const [page, setPage] = useState('home'); // 'home', 'dashboard', 'stations', 'map', 'contact'

    // State to hold the currently logged-in user's information
    const [user, setUser] = useState(null);

    // State to control the visibility and mode ('login' or 'register') of the authentication modal
    const [modalMode, setModalMode] = useState(null);

    // This effect runs once when the application first loads
    useEffect(() => {
        // Try to find a saved login token in the browser's storage
        try {
            const token = localStorage.getItem('authToken');
            if (token) {
                // If a token is found, decode it to get user info
                const decodedUser = jwtDecode(token);
                setUser({ name: decodedUser.sub.split('@')[0], email: decodedUser.sub });
            }
        } catch (error) {
            // If the token is invalid, remove it
            localStorage.removeItem('authToken');
        }
    }, []); // The empty array [] means this effect only runs on the initial render

    // Function to handle login or registration form submission
    const handleAuthSubmit = async (userData) => {
        const apiCall = modalMode === 'register' ? registerUser : loginUser;
        const response = await apiCall(userData);
        const token = response.data.token;

        localStorage.setItem('authToken', token); // Save the new token
        const decodedUser = jwtDecode(token);
        setUser({ name: userData.name || decodedUser.sub.split('@')[0], email: decodedUser.sub });
        setModalMode(null); // Close the modal
    };

    // Function to handle user logout
    const handleLogout = () => {
        localStorage.removeItem('authToken'); // Remove the token
        setUser(null); // Clear the user state
        setPage('home'); // Navigate back to the homepage
    };

    // Function to decide which page component to display
    const renderPage = () => {
        switch (page) {
            case 'dashboard':
                return user ? <DashboardPage /> : <HomePage user={user} />; // Show dashboard only if logged in
            case 'stations':
                return <ChargingStationsPage />;
            case 'map':
                return <CampusMapPage />;
            case 'contact':
                return <ContactUsPage />;
            case 'home':
            default:
                return <HomePage user={user} />;
        }
    };

    return (
        <>
            {modalMode && <AuthModal mode={modalMode} onSubmit={handleAuthSubmit} onClose={() => setModalMode(null)} />}
            <Header user={user} setPage={setPage} onLogin={() => setModalMode('login')} onRegister={() => setModalMode('register')} onLogout={handleLogout} />
            <main>
                {renderPage()}
            </main>
            <Footer setPage={setPage} />
        </>
    );
}

// --- Page & Layout Components ---

function Header({ user, setPage, onLogin, onRegister, onLogout }) {
    return (
        <nav className="nav">
            <div className="brand" onClick={() => setPage('home')} style={{ cursor: 'pointer' }}>
                <div className="logo">EV</div>
                <h1>EV Campus Connect</h1>
            </div>
            <div className="actions">
                <button className="btn" onClick={() => setPage('stations')}>Stations</button>
                <button className="btn" onClick={() => setPage('map')}>Map</button>
                <button className="btn" onClick={() => setPage('contact')}>Contact</button>
                {user ? (
                    <>
                        <button className="btn" onClick={() => setPage('dashboard')}>My Bookings</button>
                        <span className="welcome-user">Welcome, {user.name}</span>
                        <button className="btn" onClick={onLogout}>Logout</button>
                    </>
                ) : (
                    <>
                        <button className="btn" onClick={onLogin}>Login</button>
                        <button className="btn primary" onClick={onRegister}>Sign Up</button>
                    </>
                )}
            </div>
        </nav>
    );
}

function HomePage({ user }) {
    const [vehicles, setVehicles] = useState([]);
    const [filter, setFilter] = useState('all');

    useEffect(() => {
        const fetchVehicles = async () => {
            try {
                const response = await getVehicles();
                setVehicles(response.data);
            } catch (error) {
                console.error("Failed to fetch vehicles:", error);
            }
        };
        fetchVehicles();
    }, []);

    const handleBooking = async (vehicleId) => {
        try {
            await createBooking(vehicleId);
            alert('Vehicle booked successfully! Check your dashboard.');
        } catch (error) {
            const errorMessage = error.response?.data?.error || 'An unexpected error occurred.';
            alert(`Booking Failed: ${errorMessage}`);
        }
    };

    const filteredVehicles = vehicles.filter(v => filter === 'all' || v.type === filter);

    return (
        <section className="hero">
            <div className="bg" style={{backgroundImage: "url('https://images.unsplash.com/photo-1617886322207-6f504e7472c5?w=1920')"}}></div>
            <div className="content">
                <h2>Zero Emissions, Maximum Convenience</h2>
                <p>An integrated platform for shared electric vehicles and charging infrastructure on campus.</p>

                <div className="how-it-works">
                    <h3>How It Works</h3>
                    <div className="steps">
                        <div className="step"><span>1</span> Register & Login</div>
                        <div className="step"><span>2</span> Find Your Ride</div>
                        <div className="step"><span>3</span> Book Instantly</div>
                    </div>
                </div>

                <div className="vehicle-showcase">
                    <div className="filter-tabs">
                        <button onClick={() => setFilter('all')} className={filter === 'all' ? 'active' : ''}>All</button>
                        <button onClick={() => setFilter('scooter')} className={filter === 'scooter' ? 'active' : ''}>Scooters</button>
                        <button onClick={() => setFilter('bike')} className={filter === 'bike' ? 'active' : ''}>Bikes</button>
                    </div>
                    <div className="vehicle-list">
                        {filteredVehicles.length > 0 ? filteredVehicles.map(v => (
                            <div key={v.id} className="vehicle-card">
                                <img src={v.imageUrl} alt={v.name} />
                                <div className="vehicle-info">
                                    <h4>{v.name}</h4>
                                    <span>{v.range} range</span>
                                    <p>{v.offer}</p>
                                    {user && <button className="btn primary book-btn" onClick={() => handleBooking(v.id)}>Book Now</button>}
                                </div>
                            </div>
                        )) : <p>Loading vehicles from your Java backend...</p>}
                    </div>
                </div>
            </div>
        </section>
    );
}

function DashboardPage() {
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const response = await getMyBookings();
                setBookings(response.data);
            } catch (error) {
                console.error("Failed to fetch bookings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchBookings();
    }, []);

    return (
        <div className="page-container">
            <h2 className="page-header">My Bookings Dashboard</h2>
            {loading && <p>Loading your bookings...</p>}
            {!loading && bookings.length === 0 && (
                <div className="card-message">You have not made any bookings yet. Go to the homepage to book a vehicle!</div>
            )}
            <div className="booking-list">
                {bookings.map(b => (
                    <div className="booking-card" key={b.id}>
                        <img src={b.vehicleImageUrl} alt={b.vehicleName} />
                        <div className="booking-info">
                            <h4>{b.vehicleName}</h4>
                            <p>Booked on: {new Date(b.bookingTime).toLocaleString()}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

function ChargingStationsPage() {
    const stations = [
        { name: "Library Parking Garage", details: "4 Level 2 Chargers ‚Ä¢ 24/7 Access ‚Ä¢ Open to all." },
        { name: "Student Union Center", details: "2 DC Fast Chargers ‚Ä¢ 6AM - 10PM ‚Ä¢ Priority for student vehicles." },
        { name: "Science Building Lot D", details: "6 Level 2 Chargers ‚Ä¢ Faculty Only ‚Ä¢ Requires staff permit." },
        { name: "Sports Complex Lot A", details: "8 Level 2 Chargers ‚Ä¢ 24/7 Access ‚Ä¢ Pay per use." }
    ];

    return (
        <div className="page-container">
            <h2 className="page-header">Campus Charging Stations</h2>
            <div className="booking-list"> {/* Re-using booking-list style for consistency */}
                {stations.map(station => (
                    <div className="booking-card" key={station.name}>
                        <div className="booking-info">
                            <h4>{station.name}</h4>
                            <p>{station.details}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

function CampusMapPage() {
    return (
        <div className="page-container">
            <h2 className="page-header">Interactive Campus Map</h2>
            <div className="map-placeholder">
                <span>üó∫Ô∏è</span>
                <p>An interactive map of campus showing available vehicles and charging stations is coming soon.</p>
            </div>
        </div>
    );
}

function ContactUsPage() {
    const team = [
        { name: "Ayush Kushwaha", role: "Backend & Systems Architecture" },
        { name: "Atharva Gokhale", role: "Database & API Optimization" },
        { name: "Utsav Golani", role: "UI/UX Design & Frontend Logic" },
        { name: "Arav Palod", role: "Project Strategy & Debugging" },
    ];

    return (
        <div className="page-container">
            <h2 className="page-header">Meet the Team</h2>
            <p style={{color: 'var(--muted)', marginTop: '-20px', marginBottom: '30px'}}>
                We are a team of passionate student developers from TSEC, Mumbai, dedicated to creating innovative solutions for campus life.
            </p>
            <div className="contact-grid">
                {team.map(member => (
                    <div className="contact-card" key={member.name}>
                        <h4>{member.name}</h4>
                        <p>{member.role}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

function Footer({ setPage }) {
    return (
        <footer className="ev-footer">
            <div className="actions">
                <a onClick={() => setPage('home')}>Home</a> &nbsp;‚Ä¢&nbsp;
                <a onClick={() => setPage('stations')}>Charging Stations</a> &nbsp;‚Ä¢&nbsp;
                <a onClick={() => setPage('contact')}>Contact Us</a>
            </div>
            <p>¬© 2025 EV Campus Connect. A project for the students of TSEC, Mumbai.</p>
        </footer>
    );
}

export default App;
