import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode'; // <-- NEW: Import the decoder
import { registerUser, loginUser, getVehicles } from './apiService';
import AuthModal from './AuthModal';

function App() {
    const [vehicles, setVehicles] = useState([]);
    const [user, setUser] = useState(null);
    const [modalMode, setModalMode] = useState(null);

    // NEW: This effect runs once when the app loads to check for an existing login
    useEffect(() => {
        try {
            const token = localStorage.getItem('authToken');
            if (token) {
                const decodedUser = jwtDecode(token);
                // The 'sub' claim in our JWT holds the user's email
                setUser({ name: decodedUser.sub.split('@')[0] }); 
            }
        } catch (error) {
            console.error("Invalid token found in localStorage", error);
            localStorage.removeItem('authToken');
        }
    }, []);

    // This effect runs to fetch vehicles
    useEffect(() => {
        const fetchVehicles = async () => {
            try {
                const response = await getVehicles();
                setVehicles(response.data);
            } catch (error) {
                console.error("Failed to fetch vehicles:", error);
            }
        };
        fetchVehicles();
    }, []);

    const handleAuthSubmit = async (userData) => {
        const apiCall = modalMode === 'register' ? registerUser : loginUser;
        const response = await apiCall(userData);
        const token = response.data.token;

        localStorage.setItem('authToken', token);
        const decodedUser = jwtDecode(token);
        setUser({ name: userData.name || decodedUser.sub.split('@')[0] });
        setModalMode(null);
    };

    const handleLogout = () => {
        localStorage.removeItem('authToken');
        setUser(null);
    };

    return (
        <>
            {modalMode && <AuthModal mode={modalMode} onSubmit={handleAuthSubmit} onClose={() => setModalMode(null)} />}
            <Header user={user} onLogin={() => setModalMode('login')} onRegister={() => setModalMode('register')} onLogout={handleLogout} />
            <main>
                <HomePage vehicles={vehicles} />
            </main>
            <Footer />
        </>
    );
}

// All other components (Header, HomePage, Footer, etc.) remain the same.
// ... (rest of the file is unchanged) ...

function Header({ user, onLogin, onRegister, onLogout }) {
    return (
        <nav className="nav">
            <div className="brand">
                <div className="logo">EV</div>
                <h1>EV Campus Connect</h1>
            </div>
            <div className="actions">
                {user ? (
                    <>
                        <span className="welcome-user">Welcome, {user.name}</span>
                        <button className="btn" onClick={onLogout}>Logout</button>
                    </>
                ) : (
                    <>
                        <button className="btn" onClick={onLogin}>Login</button>
                        <button className="btn primary" onClick={onRegister}>Sign Up</button>
                    </>
                )}
            </div>
        </nav>
    );
}

function HomePage({ vehicles }) {
    return (
        <section className="hero">
            <div className="bg" style={{backgroundImage: "url('https://images.unsplash.com/photo-1617886322207-6f504e7472c5?w=1920')"}}></div>
            <div className="content">
                <h2>Zero Emissions, Maximum Convenience</h2>
                <p>Smart shared-rides, rentable EV two-wheelers and campus charging infrastructure — all designed to make campus life quieter and cleaner.</p>
                <div className="vehicle-showcase">
                    <h3>Our Fleet</h3>
                    <div className="vehicle-list">
                        {vehicles.length > 0 ? vehicles.map(v => (
                            <div key={v.id} className="vehicle-card">
                                <img src={v.imageUrl} alt={v.name} />
                                <div className="vehicle-info">
                                    <h4>{v.name}</h4>
                                    <span>{v.range} range</span>
                                </div>
                            </div>
                        )) : <p>Loading vehicles from your Java backend...</p>}
                    </div>
                </div>
            </div>
        </section>
    );
}

function Footer() {
    return (
        <footer className="ev-footer">
            <p>© 2025 EV Campus Connect</p>
        </footer>
    );
}


export default App;
